INSERT INTO `tours` (`id`, `language_id`, `sub_category_id`, `name`, `small_desc`, `desc`, `img`, `cover`, `img_title`, `img_alt`, `cover_title`, `cover_alt`, `price_start_from`, `inclusion`, `exclusion`, `price_table_id`, `show_in_special_offers`, `num_of_stars`, `seo`, `slug`, `sort`, `deleted_at`, `created_at`, `updated_at`) VALUES
(104, 3, 99, 'Alexandria to Abu Simbel Wheelchair Package', 'This wheelchair accessible tour package will enable you to Explore the length of Egypt Nile Valley from Alexandria on the Mediterranean to Abu Simbel on Lake Nasser. Visit Giza Pyramids, Cairo landmarks, Luxor Temples, Aswan and Abu Simbel with many more to enjoy.', '<h2 style=\"direction: ltr; \">Day 1:Arrival Cairo</h2>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">Welcome to Cairo,Egypt</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;(Around The Clock)</span><br />\r\n<span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">Your </span><span style=\"color:#0000FF\"><span style=\"background-color:rgb(255, 255, 255); font-size:14px\">Signature</span></span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;tour manager will meet and assist you at Cairo International Airport (Arrival Producer) and then he will escort you to the hotel by an accessible private air-conditioned modern van adapted for wheelchair travelers. At the hotel, the tour manager will assist with a smooth check-in and review your holiday itinerary with you to establish and confirm pick-up times for each tour. Overnight in Cairo.</span><br />\r\n<em><strong>Welcome Drink</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 2:Pyramids / Museum</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">Breakfast at your hotel in Cairo, then your personal&nbsp;</span><span style=\"color:rgb(0, 0, 255); font-family:helvetica,arial,sans-serif; font-size:14px\">Signature Tours Egypt</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\"> guide will pick you up by an accessible private air-conditioned modern van, and accompany you to Giza Plateu&nbsp;to visit one of the seven wonders of the ancient world, the Great Pyramid of Cheyops</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, Chefren</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;and Mykrienus</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, famous Sphinx and Valley Temple</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;facing the great statue. All places are wheelchair accessible. Lunch at a good quality restaurant. Then move onto the Egyptian Museum</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, well-paved place, where you can see the&nbsp;</span>collection of Tutankhamen<span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;among other splendid pharaonic artifacts. Continue driving to Islamic Cairo, where you will be able to explore Khan El Khalili</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, Cairo&rsquo;s old bazaar. Back to your hotel and overnight in Cairo</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">.</span><br />\r\n<em><strong>Meals: Breakfast, Lunch</strong></em></p>\r\n\r\n<p dir=\"ltr\">&nbsp;</p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 3:Cairo/ Alexandria Trip</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">After breakfast, check out from your hotel in Cairo</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, then move to have a tour to Alexandria</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, about 03 hours driving to see Pompeys Pillar</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, a monument of the Roman Emperor Diocletian, and Qaitbey Citadel, built in the 15th century by Sultan Qaitbay on the spot of Alexandria Ancient Lighthouse</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">. Overnight in Alexandria</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;at Sheraton Montazah Hotel, a 5-star hotel with accessible handicapped services.</span><br />\r\n<em><strong>Meals: Breakfast, Lunch</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 4:Alexandria/ Cairo</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">Free day in Alexandria</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;and spend the rest of the morning on the corniche. By the afternoon, you will be transferred back to Cairo</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, about 03 hours driving. Overnight in Cairo</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">.</span><br />\r\n<em><strong>Meals: Breakfast</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 5:Cairo/ Luxor West Bank</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">Breakfast at your hotel, then you will be transferred to Cairo Airport</span><a href=\"https://www.memphistours.com/Egypt/WikiTravel/General/wiki/Cairo-International-Airport\" style=\"box-sizing: border-box; -webkit-tap-highlight-color: transparent; background-color: rgb(253, 253, 253); color: rgb(8, 149, 255); font-family: Avenir, sans-serif; font-size: 15px; text-align: justify; text-decoration-line: none !important;\">&nbsp;</a><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">for a flight to Luxor</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;where you will be met and assisted by our representative at the airport. Check in at Sheraton Luxor Hotel, a 5-star hotel, enjoying accessible handicapped services during your stay. Then move to the west bank to see the Temple Of Queen Hatshepsut</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;and the Colossi Of Memnon</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">. Lunch will be served in a good quality local restaurant. Back to your hotel and overnight in Luxor.</span><br />\r\n<em><strong>Meals: Breakfast, Lunch</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Days 6:Luxor East Bank</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">At the morning you will be escorted by your personal&nbsp;</span><span style=\"color:rgb(0, 0, 255); font-family:helvetica,arial,sans-serif; font-size:14px\">Signature</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;guide to visit the magnificent Karnak&nbsp;and Luxor Temples</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;on the East bank. Lunch will be served at a good quality restaurant during your tour in Luxor. Back to your hotel and overnight in Luxor.</span><br />\r\n<em><strong>Meals: Breakfast, Lunch</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 7:Luxor/ Aswan Sightseeing</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">After breakfast, transfer by an accessible private air-conditioned modern van to Aswan</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;about 03 hours driving, where you will be met and taken to check in Moevenpick Aswan Resort, 05 star hotel with accessible handicapped services. Move to see the High Dam</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">,&nbsp;</span><strong>The Nubian Village</strong><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">. Transfer back to your hotel and overnight in Aswan.</span><br />\r\n<em><strong>Meals: Breakfast, Lunch</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 8:Abu Simbel Tour</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">After breakfast, at 10:30 am transfer by an accessible private air-conditioned modern van adapted for wheelchair travelers to Abu Simble</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">, about 03 hours driving. Upon arrival enjoy the impressive Temples of Ramses II</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;at Abu Simbel. Lunch will be served at a good quality local restaurant, then drive back to Aswan</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;and overnight.</span><br />\r\n<em><strong>Meals: Breakfast, Lunch</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 9:Back to Cairo</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">Transfer to Aswan Airport for flight to Cairo</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">. Upon arrival, you will be transferred to overnight at Le Meridien Pyramids Hotel 05 star deluxe hotel in Cairo.</span><br />\r\n<em><strong>Meals: Breakfast</strong></em></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-size:18px\">Day 10:Final Departure</span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">After breakfast, you will be driven by an accessible modern vehicle to Cairo International Airport</span><span style=\"background-color:rgb(253, 253, 253); font-family:avenir,sans-serif; font-size:15px\">&nbsp;for final departure.</span><br />\r\n<em><strong>Meals: Breakfast</strong></em></p>\r\n\r\n<p dir=\"ltr\">&nbsp;</p>', '20476550_1421893164556161_3631675911753719713_n.jpg', '74082_160375050662487_3271601_n.jpg', NULL, NULL, NULL, NULL, '2000.00', '<ul>\r\n	<li style=\"direction: ltr; text-align: left;\">Meet and greet service by our representatives at airports</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Assistance of our guest relations during your stay</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Accommodation for 4 nights in Cairo including breakfast</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Accommodation for 1 night in Alexandria including breakfast</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Accommodation for 2 nights in Luxor including breakfast</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Accommodation for 2 nights in Aswan including breakfast</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Domestic flight (Cairo &ndash; Luxor&ndash; Aswan - Cairo)</li>\r\n	<li style=\"direction: ltr; text-align: left;\">All sightseeing tours are private tours</li>\r\n	<li style=\"direction: ltr; text-align: left;\">All sightseeing tours in Cairo, Alexandria, Luxor, Aswan and Abu Simbel as mentioned in the itinerary</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Entrance fees to all sites as indicated in the itinerary</li>\r\n	<li style=\"direction: ltr; text-align: left;\">English speaking guide throughout your tours</li>\r\n	<li style=\"direction: ltr; text-align: left;\">All transfers by an adapted air-conditioned vehicle</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Bottled water during the trips</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Free helper during your sightseeing tours at the historical places</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Lunch during tours in Cairo, Alexandria, Luxor &amp; Aswan</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Portage when needed</li>\r\n	<li style=\"direction: ltr; text-align: left;\">30 minutes of local calling (5 minutes international) and a mobile phone loaned for the length of your stay or an Egyptian sim card for your unlocked mobile device</li>\r\n	<li style=\"direction: ltr; text-align: left;\">All service charges &amp; taxes</li>\r\n</ul>', '<ul>\r\n	<li style=\"direction: ltr; text-align: left;\">International Airfare</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Entry visa to Egypt</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Any optional tours</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Personal spendings</li>\r\n	<li style=\"direction: ltr; text-align: left;\">Tipping</li>\r\n</ul>', NULL, 'no', '5', NULL, 'alexandria-to-abu-simbe-l-wheelchair-package', 14, NULL, '2019-09-01 14:20:26', '2019-09-03 15:26:11'),
(105, 3, 100, '15 Days Honeymoon Cairo, Alexandria, Abu Simbel, Nile Cruise & Luxor', '15 Days Honeymoon Cairo, Alexandria, Abu Simbel, Nile Cruise & Luxor', '<h3 style=\"direction: ltr; \"><strong>Day 1: Cairo</strong></h3>\r\n\r\n<p style=\"direction: ltr;\">Arrival to Cairo airport, Upon you arrival you will find our representative waiting you with the sign of <span style=\"color:#0000FF\"><strong>Signature Tours Egypt</strong></span>, then you will be transferred by A/C car to your hotel. Overnight in Cairo.</p>\r\n\r\n<h3 style=\"direction: ltr; \"><strong>Day 2: Cairo</strong></h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast. Start your tour visiting the Egyptian Museum of antiquities. On display is rare collection of 5000 years of art the largest most precious collection of Egyptian art in the world. Including an exhibit dedicated to the Tutankhamen collection of treasures, gold, and jewelry.</p>\r\n\r\n<p style=\"direction: ltr;\">Visit the Great Pyramids &ndash; Cheops, Chephren &amp; Mykerions &amp; Sphinx, including a complimentary camel ride.</p>\r\n\r\n<p style=\"direction: ltr;\">(Optional) Evening trip to attend the Sound &amp; Light show in the Pyramids area.</p>\r\n\r\n<p style=\"direction: ltr;\">Overnight in Cairo.</p>\r\n\r\n<h3 style=\"direction: ltr;\"><strong>Day 3: Cairo free day</strong></h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast. Free day.</p>\r\n\r\n<div style=\"direction: ltr;\">Overnight in Cairo.</div>\r\n\r\n<div style=\"direction: ltr;\">\r\n<h3><strong>Day 4: Alexandria</strong></h3>\r\n\r\n<p>Breakfast. Travel to Alexandria. Our tour guide will pick you up from your hotel to start your tour to Alexandria. Start the tour with the library of Alexandria; the biggest library in the world &ndash; now with 8 million books. Visit it&rsquo;s open court and free museums.</p>\r\n\r\n<p>Note: Alexandria Library is closed on Fridays and on every public holiday. To enter the reading area you will need to purchase an extra ticket.</p>\r\n\r\n<p>Then visit the Site of the Lighthouse; one of the Seven Wonders of the Ancient World on which now stands the castle of Qaitbey. Visit the royal gardens of King Farouk (the last king in the history of Egypt) and see the royal palaces of Haramlek and Salamlek as well as the royal beaches of the queens. Continue your tour by visiting the prettiest and biggest mosque in the city: the Abbo Elabbas Mosque, as well as the memorial of the Unknown Soldier of Alexandria and the statue of Alexander the Great.</p>\r\n\r\n<p>You can then stop for lunch (Optional) before visiting the church of St. Mark &ndash; the oldest church in Africa before driving along the Cornish (30 km) to see the whole city of Alexandria from the sea side which includes the old harbor of Alexandria where we found 7000 monuments underwater.</p>\r\n\r\n<p>Stop over the famous bridge of Stanley &ndash; the modern landmark of modern Alexandria, before visiting the old downtown of Alexandria and walking through the old city-center of Alexandria: built by Alexander the great. Travel in comfort back to your hotel.</p>\r\n\r\n<p>Alexandria has so much to see, feel, eat and buy so if you have time and want to add more to your itinerary &ndash; just let us know what you are interested in and we will arrange it for you at the cost price &ndash; no profit to us.</p>\r\n\r\n<p>Check in &amp; overnight in Alexandria.</p>\r\n\r\n<h3><strong>Day 5: Alex free day</strong></h3>\r\n\r\n<p>Breakfast. Free day.<br />\r\nOvernight in Alexandria.</p>\r\n\r\n<h3><strong>Day 6: Alex &ndash; Cairo</strong></h3>\r\n\r\n<p>Breakfast. Transfer to Cairo by an A/C car. Arrival and transfer to your hotel.</p>\r\n\r\n<p>Overnight in Cairo.</p>\r\n\r\n<h3><strong>Day 7: Sakkara tour</strong></h3>\r\n\r\n<p>Breakfast. Visit the Step Pyramids of Zoser, the world&rsquo;s oldest major stone structure. It was built in the 3rd Dynasty (around 2630 BC) for King Djoser.</p>\r\n\r\n<p>After that transfer to Memphis which was founded by King Menes, and was the capital of Old Egypt during the Old Kingdom, it was considered to be a center of rule and culture for over 3000 years the first capital of Egypt, and see the Statue of Ramses II. On your way to the south you will visit the Red Pyramid and Bent Pyramid at Dahshur. Overnight in Cairo.</p>\r\n\r\n<h3><strong>Day 8: Citadel &amp; Old Cairo</strong></h3>\r\n\r\n<p>Breakfast. Our&nbsp;<span style=\"color:rgb(0, 0, 255); font-family:helvetica,arial,sans-serif; font-size:14px\">Signature Tours Egypt</span> guide will pick you from your hotel. You will get to visit Salah El Din Citadel which was constructed by Salah El Din on the Moqattam hills in 1183 AD to defend Cairo from the armies of Crusaders.</p>\r\n\r\n<p>After that visit Mohamed Ali Alabaster Mosque which was designed by the architect Yousif Boushnaq, a Turkish man who came specially from Istanbul and built this great mosque for Mohamed Ali, the ruler of Egypt (1805-1849) who ruled Over 45 years.</p>\r\n\r\n<p>Then proceed to Visit Old Cairo where you will enjoy, the Hanging Church, Ben Ezra Synagogue, Church of St. Barbara, Church of Abu Serga and back to hotel. Overnight in Cairo.</p>\r\n\r\n<h3><strong>Day 9: Cairo &ndash; Aswan</strong></h3>\r\n\r\n<p>Breakfast. Transfer to take the flight to Aswan. Arrival and transfer to the hotel. Check in. Overnight in Aswan.</p>\r\n\r\n<h3><strong>Day 10: Abu Simbel tour</strong></h3>\r\n\r\n<p>Breakfast. Departure to visit the Temple of Abu Simbel, The colossal Temple of Abu Simbel built by Ramses II (XIXth. Dynasty) and lately saved from inundation of the Nile waters in among the glories of ancient Egyptian monuments.</p>\r\n\r\n<p>The temples were built out of a sandstone rock cliff, and representation of the deities to which each was dedicated is carved on its huge fa&ccedil;ade. Nearby, also saved, lies the small Temple of the King&rsquo;s wife Nefertari, dedicated to the goddess Hathor. Back to Aswan &amp; overnight.</p>\r\n\r\n<h3><strong>Day 11: Cruise</strong></h3>\r\n\r\n<p>Breakfast. Transfer to visit the High Dam and the Philae Temple. Transfer to the Cruise. Boarding. Lunch on board Sail to Kom Ombo. Afternoon Tea. Visit the Temple shared by the two gods Sobek and Haroeris. Sail to Edfu. Dinner on board. Overnight in Edfu.</p>\r\n\r\n<h3><strong>Day 12: Cruise</strong></h3>\r\n\r\n<p>Breakfast on board. Visit The Temple of Horus in Edfu. Cross the lock. Sail to Luxor. Afternoon Tea. Dinner on board &amp; Galabya party. Overnight in Cruise.</p>\r\n\r\n<h3><strong>Day 13: Cruise</strong></h3>\r\n\r\n<p>Breakfast on board. Cross to the West Bank to visit the Valley of the Kings, the Temple of Hatshepsut and the Colossi of Memnon Excursion to the Necropolis of Thebes, drive to the Valley of Kings. Overnight in Luxor.</p>\r\n\r\n<h3><strong>Day 14: Cruise- Luxor</strong></h3>\r\n\r\n<p>Breakfast on board. Disembarkation.</p>\r\n\r\n<p>(Optional) Hot Balloon: Should you want to make your stay in Luxor an unforgettable. Drifting peacefully over the Valley of King, Valley of the Queens and the Mountain on the Nobles.<br />\r\nThe flight is approximately 30:45 minutes depend on the weather</p>\r\n\r\n<p>East Bank to visit the Karnak and Luxor Temple. Transfer to the hotel &amp; overnight in Luxor.</p>\r\n\r\n<h3><strong>Day 15: Luxor &ndash; Departure</strong></h3>\r\n\r\n<p>Breakfast at the hotel our representative will transfer you to the airport and assist you at the airport through final departure formalities.</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>', '64440703_2344150142573311_2928921735979859968_n.jpg', '19961613_1956801324608781_5542695370395510508_n.jpg', NULL, NULL, NULL, NULL, '1435.00', '<p>&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">6 Nights hotel accommodation in Cairo on bed and breakfast basis.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">2 Nights hotel accommodation in Alexandria on bed and breakfast basis.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">2 Nights hotel accommodation in Aswan on bed and breakfast basis.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">1 Night hotel accommodation in Luxor on bed and breakfast basis.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">3 Nights on Cruise board (Aswan &amp; Luxor) full board (sightseeing included).</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">All transfers in Cairo, Alexandria, Luxor and Aswan.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">1 Day tour in Cairo visiting the Pyramids, Sphinx, Egyptian.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">1 Day tour in Cairo visiting Memphis &amp; Sakkara.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">1 Day tour in Cairo visiting the Citadel &amp; Old Cairo.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">1 Day tour in Alexandria.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Private tour to Abu Simbel.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">All your tours and excursions are by an A/C vehicle.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">The service of meet and assist at all your destinations.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">All your visits include entrance fees.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Our prices include all taxes and services.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Transfer by A/C car: Cairo / Alexandria / Cairo.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Domestic flight: Cairo / Aswan.</span></div>', '<p style=\"direction: ltr; \"><span style=\"font-family:montserratregular; font-size:16px\">Visa Entry for Egypt. (We can provide you with your visa upon arrival at your request).</span></p>\r\n\r\n<p style=\"direction: ltr; \"><span style=\"font-family:montserratregular; font-size:16px\">International Flights.</span></p>\r\n\r\n<p style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Personal items.</span></p>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Tipping.</span></div>\r\n\r\n<div style=\"direction: ltr;\">&nbsp;</div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Any other items not mentioned.</span></div>\r\n\r\n<div style=\"direction: ltr;\">&nbsp;</div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Any Optional tours.</span></div>', NULL, 'no', '5', NULL, 'honeymoon-cairo-alexandria-abu-simbel-nile-cruise-luxor', 15, NULL, '2019-09-02 10:31:31', '2019-09-03 15:11:32'),
(106, 3, 100, 'Pyramids‚Äô Best Kept Secrets & Nubian Adventure', 'On this 8-days tour see the highlights of Egypt. Visit the Giza Pyramids and enjoy entering inside the great pyramids to discover its secrets and the Sphinx, the Egyptian Museum, before heading south to Aswan.\r\n\r\nSee the Philae temple & Nubian Village. Discover the colorful culture of Nubia, one of Africa\'s oldest civilizations. Many travelers who visit Egypt have special interest in learning about Nubians and their community. This is because of their habits and style of living that is different from any other community in Egypt. Many travellers enjoy visiting Nubian villages and having traditional Nubian meals.\r\n\r\nThen move to Luxor to visit Luxor Museum, Luxor temple & Karnak temple in the East bank, See Hatshepsut Temple, the Valley of the Kings & Queens and enjoy visiting King Tut‚Äôs tomb which you will visit from inside.\r\n\r\nThen fly back to Cairo and before your final departure treat your taste buds with delicious local cuisine and enjoy the richness of the Egyptian culture.', '<h3 style=\"direction: ltr; \">Day 1: Arrival to Cairo</h3>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Arrival to Cairo airport, Upon you arrival you will find our representative waiting you with the sign of <span style=\"color:#0000FF\">Signature Tours Egypt</span>, From the moment you arrive, your tour leader will give you full attention. Once in the car, he will give you an overview of your program as well as other aspects of your tour that will interest you. Then you will be transferred by an A/C car to your hotel.</div>\r\n\r\n<div style=\"direction: ltr;\">Overnight in Cairo.</div>\r\n\r\n<h3 style=\"direction: ltr;\">Day 2: Giza Pyramids (Inside) / Egyptian Museum &ndash; Mummies Experience</h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast, start your tour visiting visit the Great Pyramids &ndash; Cheops, Chephren &amp; Mykerions.&nbsp;<em><strong>Entering inside the Great Pyramid is an amazing&nbsp;experience that&rsquo;s why it&rsquo;s the highlight of our itinerary.</strong></em></p>\r\n\r\n<p style=\"direction: ltr;\">Probably the most overwhelming experience you will have in your life, getting into the interior of the great pyramid. You will never see something like it.</p>\r\n\r\n<p style=\"direction: ltr;\">As you might expect, one of the greatest unsolved mysteries of ancient times is the glorious Pyramids of Giza. Somewhere far away in the secluded desert, prepare yourself &nbsp;to be amazed as you will discover the secrets of the Great Pyramid &ndash; the last standing ancient wonder of the world.</p>\r\n\r\n<p style=\"direction: ltr;\">Enjoy your&nbsp;<strong>Camel Ride</strong>; you will enjoy special panoramic views of the Sphinx &amp; Pyramids. Hear about all the hidden stories of Pharaohs life. Our professional tour guides will give you all the details so you can enjoy to the maximum your visit to the pyramids area.</p>\r\n\r\n<p style=\"direction: ltr;\">Then move to visit the Egyptian Museum of antiquities. Over 250,000 genuine artifacts are presented, including an exhibit dedicated to the Tutankhamen collection of treasures, gold, and jewelry, as it was enclosed in his tomb for over 3,500 years before it was discovered in the 1920s when his tomb was excavated. Includes entry to&nbsp;<strong>the royal mummy&rsquo;s hall</strong>.</p>\r\n\r\n<p style=\"direction: ltr;\">It has a separate section where royal mummies unearthed from various tombs are preserved. The room is climate controlled and the mummies appear to be looked after well. Mummified over 5000 years ago, you can see the mummies of Queen Hatshepsut, Tuthmosis and Ramses in a very good condition. Walk around leisurely in this room and look at Pharaohs and queen resting in peace.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">(Optional) Evening trip to attend the Sound &amp; Light show in the Pyramids area.</div>\r\n\r\n<div style=\"direction: ltr;\">Overnight in Cairo.</div>\r\n\r\n<h3 style=\"direction: ltr;\">Day 3: Experience The Nubian Life</h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast. Transfer to the domestic airport. Departure by plane to Aswan.</p>\r\n\r\n<p style=\"direction: ltr;\">Transfer to&nbsp;<em><strong>a unique Nubian resort (completely different world)</strong></em>&nbsp;set in the heart of a Nubian village which is a relaxing oasis by the side of the Nile. Live the Nubian life! The architecture, the design and the interiors of the resort as well as its location along the Nile allows for a very enriching experience.</p>\r\n\r\n<p style=\"direction: ltr;\">Discover the colorful culture of Nubia, one of Africa&rsquo;s oldest civilizations. Many travelers who visit Egypt have special interest in learning about Nubians and their community. This is because of their habits and style of living that is different from any other community in Egypt. Many travellers enjoy visiting Nubian villages and having traditional Nubian meals.</p>\r\n\r\n<p style=\"direction: ltr;\">We will offer a rich cultural experience; you&rsquo;ll get to know Nubian culture and gain insight into the traditions the Nubians have followed for thousands of years.</p>\r\n\r\n<p style=\"direction: ltr;\"><em><strong>Overnight in Nubian Resort.</strong></em></p>\r\n\r\n<p style=\"direction: ltr;\">Day 4: Aswan / Cruise</p>\r\n\r\n<p style=\"direction: ltr;\"><em><strong>Wake up with a wonderful view of the Nile and enjoy a Nubian traditional breakfast made by the Nubian people.</strong></em></p>\r\n\r\n<p style=\"direction: ltr;\">Then move to visit and the Philae temple &amp; the high dam.</p>\r\n\r\n<p style=\"direction: ltr;\">Transfer to the cruise. Overnight in Cruise.</p>\r\n\r\n<p style=\"direction: ltr;\"><strong>Note: Cruise schedules may change due to some sailing reasons. We guarantee that you will do all the mentioned sights, but the order may vary.</strong></p>\r\n\r\n<h3 style=\"direction: ltr;\">Day 5: Cruise</h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast on board. Visit the amazing temple of KomOmbo. Afternoon tea. Visit the temple shared by the two gods Sobek and Haroeris. Sail to Edfu.Dinner on board. Luxor. Afternoon Tea.Dinner on board &amp;Galabya party.Overnight in Cruise.</p>\r\n\r\n<h3 style=\"direction: ltr;\">Day 6: East Bank: Karnak &amp; Luxor Temples</h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast on board. Then transfer to visit the Karnak Temples, the greatest example of worship in history and was dedicated to the God Amon, his wife Mut and their son Khonsu. It was constructed from the beginning of the Middle Kingdom to the Graeco Roman Kingdom.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Then move to Luxor Temple. It was built by Amunhotep III in 18th Dynasty in the New Kingdom and it was completed by Ramesses II during the 19th Dynasty.</div>\r\n\r\n<div style=\"direction: ltr;\">King Ramses II was the most famous king in the 19th Dynasty. He ruled Egypt for about 67 years. It was dedicated to the great god Amun-Ra, his wife Mut and their son Khonsu together representing the Theban triad. Enjoy visiting Obelisk of Ramses II in front of the 1st Pylon.Overnight in Cruise.</div>\r\n\r\n<h3 style=\"direction: ltr;\">Day 7: Luxor King Tut&rsquo;s tomb &ndash; Valley of Kings &amp; Queens</h3>\r\n\r\n<p style=\"direction: ltr;\">Breakfast on board. Disembarkation.</p>\r\n\r\n<p style=\"direction: ltr;\">Cross to the West Bank to visit Valley of the Kings, visit the tombs of various Dynasties.&nbsp;<strong>King Tut&rsquo;s tomb</strong>&nbsp;which you will visit from inside.Tutankhamun ruled Egypt for approximately 10 years from around 1336-1327 BCE. In November 1922, Howard Carter and Lord Carnarvon discovered Tutankhamun&rsquo;s near-intact tomb in the Valley of the Kings. The king&rsquo;s mummified body was found surrounded by precious grave goods, in his golden coffin, after his burial chamber was officially opened on 17 February 1923 in the presence of Egyptologists and government officials.</p>\r\n\r\n<p style=\"direction: ltr;\">Today, Tutankhamun is ancient Egypt&rsquo;s most famous pharaoh. But how much do you really know about the boy king? Here during this tour, I will tell you the lesser-known facts about Tutankhamun&hellip;</p>\r\n\r\n<p style=\"direction: ltr;\">Continue to Visit&nbsp;<strong>the Valley of the Queens</strong>, known as Ta-Set-Neferu &ldquo;The Place of Beauty&rdquo;. The valley served as a necropolis for the tombs of royal family members and the elite, with about 90 tombs belonging to Queens and princes of the ancient Egyptian New Kingdom (1550-1070 BC). whilst here, you will have a chance to visit the magnificent colorful tomb of queen Nefertari (Extra Ticket)</p>\r\n\r\n<p style=\"direction: ltr;\">Then move to visit the Temple of Deir El Bahari of Queen Hatshepsut. On the return journey stop at the Colossi of Memnon which are two gigantic sitting statues representing Amenopis III facing the Nile. Then visit the Temple of Queen Hatshepsut.</p>\r\n\r\n<p style=\"direction: ltr;\">Then Transfer to the airport. Departure by plan to Cairo. Arrival and transfer to your hotel. Overnight in Cairo.</p>\r\n\r\n<h3 style=\"direction: ltr;\">Day 8: Cairo Food Tour &ndash; Final Departure</h3>\r\n\r\n<p style=\"direction: ltr;\"><strong>If your departure flight time allows</strong>&nbsp;you&rsquo;ll make this tour today if not we will fit it into another day earlier.</p>\r\n\r\n<p style=\"direction: ltr;\">To enjoy the real taste of Egypt you have to try the Egyptian food the best foods in Egypt which is one of the life&rsquo;s great pleasures, Discover off-the-beaten-path spots the real Egypt that is not covered in any guidebook.</p>\r\n\r\n<p style=\"direction: ltr;\">To find the best place to eat in Egypt can be a challenge. Don&rsquo;t worry we&rsquo;ll take you to them, There are many delicious food tastings of the Best foods in Egypt.</p>\r\n\r\n<p style=\"direction: ltr;\">Then you will be transferred to Airport for final departure.</p>', '19961613_1956801324608781_5542695370395510508_n.jpg', '64977910_2344149742573351_1154260555150655488_n.jpg', NULL, NULL, NULL, NULL, '1275.00', '<p>&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">3 Nights hotel accommodation in Cairo on bed and breakfast basis.1 Night accommodation in Nubian Resort .</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">3 Nights on Cruise board; Aswan &amp; Luxor full board (Sightseeing are included).</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">1 Day tour in Cairo visiting the Egyptian Museum, Great Pyramids, Sphinx</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">All transfers</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">All your tours and excursions are by an A/C vehicle.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">English speaking expert Egyptologist guide.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">All your visits include entrance fees.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Our prices include all taxes and services.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Domestic Ticket from Cairo / Aswan &amp; Luxor / Cairo</span></div>', '<p>&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">International Airfare.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Egypt entry visa.(We can provide you with your visa upon arrival at your request).</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Drinks and any personal expenses.</span></div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">Tipping</span></div>', NULL, 'no', '5', NULL, 'cairo-aswan-luxor-egyptian-museum-great-pyramids-sphinx', 16, NULL, '2019-09-02 11:22:48', '2019-09-03 15:14:52'),
(107, 3, 100, '9 Days Signature Egypt: Nubia & the Nile (Ultimate Luxury)', 'Special Offer: Internal Air included in the price\r\nPRIVATE TOUR\r\n\r\nRide a camel past the pyramids at Giza, Internal Air included in the price, Cruise the Nile aboard Sanctuary \'Sun Boat IV,\' visit the Nubian village and Abu Simble Temples and other fascinating destinations.\r\n\r\nEmbark on a three-night Nile River cruise aboard award-winning Sanctuary ‚ÄòSun Boat IV‚Äô\r\nAvoid crowds and other ships with private Nile docking facilities in Luxor and Aswan\r\nIn Aswan, cross the Nile to visit a traditional Nubian village and meet a local family\r\nExplore the temples of Ramses II and Queen Nefertari in Abu Simbel', '<p style=\"direction: ltr; \">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 1</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>ARRIVE CAIRO, EGYPT</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Arrive in Cairo, where you are met <span style=\"color:#0000FF\">Signature Tours Egypt</span> manager (<strong>visa is included)&nbsp;</strong>and transferred to your hotel. After settling in, depart with your Egyptologist for an afternoon visit to the Giza Plateau, site of the ancient pyramids of Mycerinus, Chefren and the Great Pyramid of Cheops. Explore the interior of one of these legendary pyramids. Ride Like a Local on camel back, continuing on to explore the funerary boat exhibit of the Solar Boat Museum. Then, view the Great Sphinx, the ancient world&rsquo;s largest monumental sculpture, carved from a single ridge of limestone.</div>\r\n\r\n<div style=\"direction: ltr;\">Evening trip to attend the Sound &amp; Light show in the Pyramids area included.</div>\r\n\r\n<div style=\"direction: ltr;\"><span style=\"font-family:montserratregular; font-size:16px\">The Nile Ritz-Carlton, Cairo</span><br />\r\nMeals: Lunch</div>\r\n\r\n<p style=\"direction: ltr;\"><strong>DAY 2</strong></p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>CAIRO | ECHOES OF THE PHARAOHS</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">Take in the world&rsquo;s largest collection of ancient Egyptian artifacts at the Museum of Egyptian Antiquities, exploring treasures from the tomb of Tutankhamun and the mummified remains of Ramses II. After lunch, explore the Mosque and Madrasa of Sultan Hassan, among the finest examples of early Mamluk architecture. Drive to Khan el-Khalili Bazaar, where tightly packed stalls display everything from spices and leather goods to jewelry and birds. Stroll down the main street of the old walled city of Cairo, seeing beautiful Islamic monuments and busy local markets.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">The Nile Ritz-Carlton, Cairo</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast, Lunch</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 3</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>ASWAN | IN THE FOOTSTEPS OF NOBLES &amp; MONKS</strong></strong></div>\r\n\r\n<div style=\"direction: ltr;\">&nbsp;</div>\r\n\r\n<p style=\"direction: ltr;\">Fly to Aswan (internal flights included) and visit the Tombs of the Nobles by camel. The tombs mark the final resting places of powerful governors and priests who controlled commerce between Aswan and Nubia. Proceed to Saint Simeon&rsquo;s Monastery, one of Egypt&rsquo;s best-preserved original Christian strongholds.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Sofitel Legend Old Cataract Aswan</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 4</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>ASWAN | NUBIAN TRADITIONS</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">Cruise across the Nile to visit a traditional Nubian village and meet a local family. Learn about their lifestyle and culture, gaining insights into enduring traditions spanning thousands of years.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Sofitel Legend Old Cataract Aswan</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 5</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>CRUISING THE NILE | TEMPLES OF RAMSES II &amp; NEFERTARI</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">Fly to Abu Simbel, where you visit the twin temples of Ramses II and Queen Nefertari. In Aswan, board award-winning Sanctuary &lsquo;Sun Boat IV&rsquo; your luxurious home for the next three nights. After lunch onboard, join your Egyptologist to explore a granite quarry that supplied the ancient Egyptians. Take in an enormous monolith known as the &ldquo;Unfinished Obelisk,&rdquo; a fine example of ancient Egyptian stonemasonry. Return to Sanctuary &lsquo;Sun Boat IV&rsquo; for afternoon tea. Later this evening, enjoy cocktails and dinner aboard.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Sanctuary &lsquo;Sun Boat IV&rsquo;</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast, Lunch, Dinner</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 6</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>CRUISING THE NILE | MAJESTIC PHILAE &amp; KOM OMBO</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">This morning, journey via motor launch to the island of Agilika. Here, visit Philae, a painstakingly transplanted and reconstructed temple originally dedicated to the Egyptian goddess Isis. Return to Sanctuary &lsquo;Sun Boat IV&rsquo; for a Chefs Table culinary experience exploring local ingredients. Disembark in Kom Ombo to visit a Greco-Roman temple dedicated to Haroeris (the sun god) and Sobek (the crocodile god). Enjoy afternoon tea aboard as you sail for Edfu. Later, visit the beautifully preserved Temple of Horus (the falcon god). This evening, enjoy an Egyptian-themed costume party aboard.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Sanctuary &lsquo;Sun Boat IV&rsquo;</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast, Lunch, Dinner</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 7</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>CRUISING THE NILE | KARNAK &amp; THE VALLEY OF THE KINGS</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">Cruise to Luxor, disembarking to explore the temple and statues of the east bank beginning with a visit to the Temple of Luxor. At the Temple of Karnak, walk through the Great Hypostyle Hall, taking in its expertly aligned columns. After lunch on board, explore the west bank of the Nile and the famous Valley of the Kings, the burial site of the Great Pharaohs of the New Kingdom. Discover the terraced funerary temple of Queen Hatshepsut. Time permitting, visit the Valley of the Queens (Biban el-Harim). Return to Sanctuary &lsquo;Sun Boat IV&rsquo; for a festive dinner celebrating your final night aboard.</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">Sanctuary &lsquo;Sun Boat IV&rsquo;</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast, Lunch, Dinner</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 8</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>CAIRO | PYRAMIDS OF SAKKARA</strong></strong></div>\r\n\r\n<div style=\"direction: ltr;\">Disembark Sanctuary &lsquo;Sun Boat IV&rsquo; and fly to Cairo. Drive to Memphis, taking in an open-air museum and the giant statue of Ramses II. Continue to Sakkara, home to hundreds of tombs and 14 pyramids, including the famous Step Pyramid of Djoser, the largest building of its time.</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">The Nile Ritz-Carlton, Cairo</div>\r\n\r\n<div style=\"direction: ltr;\">Meals: Breakfast</div>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\"><strong>DAY 9</strong></div>\r\n\r\n<div style=\"direction: ltr;\"><strong><strong>DEPART CAIRO</strong></strong></div>\r\n\r\n<p style=\"direction: ltr;\">After breakfast, transfer to the airport and depart.</p>\r\n\r\n<p style=\"direction: ltr;\">Meals: Breakfast</p>\r\n\r\n<p style=\"direction: ltr;\">&nbsp;</p>\r\n\r\n<div style=\"direction: ltr;\">&nbsp;</div>', '12814262_10153594524059541_7536364320105667730_n.jpg', '69177749_1069605699908695_1352718289385553920_n.jpg', NULL, NULL, NULL, NULL, '5265.00', '<ul style=\"list-style-type:none\">\r\n	<li style=\"text-align:left\">A Ready-to-book, Private Itinerary Designed for You</li>\r\n	<li style=\"text-align:left\">Sim card so you can use while in Egypt.</li>\r\n	<li style=\"text-align:left\">Domestic Flights</li>\r\n	<li style=\"text-align:left\">visa upon arrival included</li>\r\n	<li style=\"text-align:left\">complimentary camel ride</li>\r\n	<li style=\"text-align:left\">Luxury hotel accommodations as mentioned in our itinerary</li>\r\n	<li style=\"text-align:left\">Exclusive Insider-Access Opportunities</li>\r\n	<li style=\"text-align:left\">English-Speaking Local Guides</li>\r\n	<li style=\"text-align:left\">Private Sightseeing</li>\r\n	<li style=\"text-align:left\">Airport Meet and Greet with Private Transfers</li>\r\n	<li style=\"text-align:left\">Full Breakfast Daily</li>\r\n	<li style=\"text-align:left\">Entrance Fees and Taxes</li>\r\n	<li style=\"text-align:left\">24/7 On-Call Support</li>\r\n</ul>', '<ul style=\"list-style-type:none\">\r\n	<li style=\"text-align:left\">International Flights</li>\r\n</ul>', NULL, 'no', '5', NULL, 'egypt-nubia-the-nile-temples-of-ramses-ii-and-queen-nefertari-in-abu-simbel-luxor-and-aswan-temple-of-queen-hatshepsut', 17, NULL, '2019-09-02 12:09:47', '2019-09-03 13:51:09');
